         }
      },
      methods: {
         onVerify: function(recaptchaToken) {
            Form.onVerify(this, recaptchaToken)  
         },

          formIsValid() {
            if(Form.formIsValid(this)){
               return true
            }
            return false
         },

         submitForm() {
            console.log('sublit form')
            Form.submitForm(this)
         }

      }
   }
</script>

